package com.kitm.library.backend;

import com.google.common.collect.ImmutableSet;
import com.kitm.library.api.author.dto.AuthorDto;
import com.kitm.library.api.author.dto.UpsertAuthorDto;
import com.kitm.library.api.book.dto.BookDto;
import com.kitm.library.api.book.dto.UpsertBookDto;
import com.kitm.library.api.category.dto.CategoryDto;
import com.kitm.library.api.category.dto.UpsertCategoryDto;
import com.kitm.library.api.order.dto.CreateOrderDto;
import com.kitm.library.api.role.IRoleService;
import com.kitm.library.api.role.dto.CreateRoleDto;
import com.kitm.library.api.user.dto.CreateUserDto;
import com.kitm.library.api.user.dto.UserDto;
import com.kitm.library.backend.domain.author.AuthorService;
import com.kitm.library.backend.domain.book.BookService;
import com.kitm.library.backend.domain.category.CategoryService;
import com.kitm.library.backend.domain.order.OrderService;
import com.kitm.library.backend.domain.user.UserService;
import lombok.RequiredArgsConstructor;
import org.springframework.beans.factory.annotation.Value;
import org.springframework.boot.context.event.ApplicationReadyEvent;
import org.springframework.context.ApplicationListener;
import org.springframework.stereotype.Component;

import java.util.List;
import java.util.UUID;
import java.util.stream.IntStream;

/**
 * @author votuscode (https://github.com/votuscode)
 * @version 1.0
 * @since 12.02.22
 */
@Component
@RequiredArgsConstructor
public class DatabaseInitializer implements ApplicationListener<ApplicationReadyEvent> {

  private final IRoleService roleService;

  private final UserService userService;

  private final CategoryService categoryService;

  private final AuthorService authorService;

  private final BookService bookService;

  private final OrderService orderService;

  @Value("${admin.password}")
  private String adminPassword;

  @Value("${user.password}")
  private String userPassword;

  private void createRoles() {

    roleService.createOne(CreateRoleDto.builder()
        .name("ADMIN")
        .build());

    roleService.createOne(CreateRoleDto.builder()
        .name("USER")
        .build());
  }

  private void createGeneric() {

    final List<CategoryDto> categories = IntStream.range(1, 9)
        .mapToObj(index -> categoryService.createOne(UpsertCategoryDto.builder()
            .name("Category #" + index)
            .description("Lorem ipsum dolor sit amet, consectetur adipisicing elit. Provident, veritatis.")
            .build()))
        .toList();

    final List<AuthorDto> authors = IntStream.range(1, 9)
        .mapToObj(index -> authorService.createOne(UpsertAuthorDto.builder()
            .name("Author #" + index)
            .description("Lorem ipsum dolor sit amet, consectetur adipisicing elit. Provident, veritatis.")
            .build()))
        .toList();

    IntStream.range(11, 99).forEach(index -> {
      final UUID authorId = authors.get(index / 13).getId();
      final UUID categoryId = categories.get(index % 7).getId();

      bookService.createOne(UpsertBookDto.builder()
          .name("Book #" + index)
          .description("Lorem ipsum dolor sit amet, consectetur adipisicing elit. Provident, veritatis.")
          .pages(index * 9)
          .isbn("555-01234567" + index)
          .image("data:image/jpeg;base64,")
          .categoryId(categoryId)
          .authorId(authorId)
          .build());
    });
  }

  @Override
  public void onApplicationEvent(ApplicationReadyEvent applicationReadyEvent) {

    createRoles();

    userService.createOne(CreateUserDto.builder()
        .username("admin")
        .name("John Doe")
        .email("john.doe@mail.com")
        .passwordOriginal(adminPassword)
        .passwordConfirmation(adminPassword)
        .roles(ImmutableSet.of("ADMIN", "USER"))
        .build());

    final UserDto userDto = userService.createOne(CreateUserDto.builder()
        .username("user")
        .name("Diego del Morao")
        .email("diego.del.morao@mail.com")
        .passwordOriginal(userPassword)
        .passwordConfirmation(userPassword)
        .roles(ImmutableSet.of("USER"))
        .build());

    final CategoryDto categoryDto = categoryService.createOne(UpsertCategoryDto.builder()
        .name("Programming")
        .description("Books about programming")
        .build());

    final AuthorDto authorDto = authorService.createOne(UpsertAuthorDto.builder()
        .name("Robert C. Martin")
        .description("Colloquially called Uncle Bob, is an American software engineer, instructor, and best-selling author. He is most recognized for developing many software design principles and for being a founder of the influential Agile Manifesto.")
        .build());

    final BookDto bookDto = bookService.createOne(UpsertBookDto.builder()
        .name("Clean Code: A Handbook of Agile Software Craftsmanship")
        .description("Even bad code can function. But if code isn't clean, it can bring a development organization to its knees. Every year, countless hours and significant resources are lost because of poorly written code. But it doesn't have to be that way.")
        .pages(464)
        .isbn("978-0132350884")
        .image("data:image/jpeg;base64,")
        .categoryId(categoryDto.getId())
        .authorId(authorDto.getId())
        .build());

    bookService.createOne(UpsertBookDto.builder()
        .name("Clean Architecture: A Craftsman's Guide to Software Structure and Design (Robert C. Martin Series)")
        .description("By applying universal rules of software architecture, you can dramatically improve developer productivity throughout the life of any software system. ")
        .pages(456)
        .isbn("978-0134494166")
        .image("data:image/jpeg;base64,")
        .categoryId(categoryDto.getId())
        .authorId(authorDto.getId())
        .build());

    bookService.createOne(UpsertBookDto.builder()
        .name("Clean Craftsmanship: Disciplines, Standards, and Ethics (Robert C. Martin Series)")
        .description("Uncle Bob brings together the disciplines, standards, and ethics you need to deliver robust, effective code and to be proud of all the software you write.")
        .pages(467)
        .isbn("978-0136915713")
        .image("data:image/jpeg;base64,")
        .categoryId(categoryDto.getId())
        .authorId(authorDto.getId())
        .build());

    bookService.createOne(UpsertBookDto.builder()
        .name("Code That Fits in Your Head : Heuristics for Software Engineering (Robert C. Martin Series)")
        .description("Code That Fits in Your Head offers indispensable, practical advice for writing code at a sustainable pace and controlling the complexity that causes projects to spin out of control.")
        .pages(489)
        .isbn("978-0137464401")
        .image("data:image/jpeg;base64,")
        .categoryId(categoryDto.getId())
        .authorId(authorDto.getId())
        .build());

    createGeneric();

    orderService.createOne(CreateOrderDto.builder()
        .bookId(bookDto.getId())
        .userId(userDto.getId())
        .build());
  }
}
